import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  // 사용자를 조회하는 함수
  useEffect(() => {
    axios.get('http://localhost:8011/api/users')
      .then(response => {
        setUsers(response.data); // 응답 받은 데이터로 사용자 목록 업데이트
      })
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  // 새로운 사용자 추가하는 함수
  const handleAddUser = () => {
    axios.post('http://localhost:8011/api/users', { name, email })
      .then(response => {
        setUsers([...users, response.data]); // 새로운 사용자 추가
      })
      .catch(error => console.error('Error adding user:', error));
  };

  return (
    <div>
      <h1>User List</h1>
      <ul>
        {users.map(user => (
          <li key={user.id}>{user.name} ({user.email})</li>
        ))}
      </ul>

      <h2>Add User</h2>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={e => setName(e.target.value)}
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
      />
      <button onClick={handleAddUser}>Add User</button>
    </div>
  );
}

export default App;
