import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const UserListContainer = styled.div`
  padding: 2rem;
  background-color: #f4f4f4;
`;

const Table = styled.table`
  width: 100%;
  margin-top: 2rem;
  border-collapse: collapse;
  text-align: left;
`;

const TableHeader = styled.th`
  padding: 1rem;
  background-color: #333;
  color: white;
  font-size: 1.1rem;
`;

const TableData = styled.td`
  padding: 1rem;
  border: 1px solid #ddd;
`;

const Button = styled.button`
  padding: 0.5rem 1rem;
  background-color: #333;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const PageButton = styled.button`
  padding: 0.5rem;
  margin: 0.5rem;
  background-color: #333;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const PageNumber = styled.button`
  padding: 0.5rem;
  margin: 0.5rem;
  background-color: ${props => (props.active ? '#333' : '#ccc')};
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const List = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
	const [page, setPage] = useState(0); // 현재 페이지
  const [size, setSize] = useState(3); // 페이지 크기 (한 번에 보여줄 사용자 수)
  const [totalPages, setTotalPages] = useState(0); // 전체 페이지 수

  // 서버에서 사용자 목록을 가져오는 함수
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        // const response = await axios.get('http://localhost:8011/api/users/');
				const response = await axios.get(`http://localhost:8011/api/users?page=${page}&size=${size}`);
        setUsers(response.data.content); // 'content'는 페이징된 데이터
        setTotalPages(response.data.totalPages); // 전체 페이지 수
        setLoading(false);
      } catch (error) {
        setError('Error fetching users');
        setLoading(false);
      }
    };

    fetchUsers();
  }, [page, size]);

	const handleDelete = async (id) => {
		const confirmDelete = window.confirm('Are you sure you want to delete this member?');
    if(confirmDelete) {
			try {
				await axios.delete(`http://localhost:8011/api/users/${id}`);
				setUsers(users.filter((user) => user.id !== id)); // 삭제된 회원을 리스트에서 제거
				window.location.reload();
			} catch (error) {
				alert('Failed to delete user');
			}
		}
  };

	const handlePageClick = (pageNumber) => {
    setPage(pageNumber); // 클릭한 페이지 번호로 페이지 변경
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <UserListContainer>
      <h2>User List</h2>
      <Table>
        <thead>
          <tr>
            <TableHeader>Name</TableHeader>
            <TableHeader>Email</TableHeader>
            <TableHeader>Password</TableHeader>
						<TableHeader>Actions</TableHeader>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
							<TableData>{user.name}</TableData>
              <TableData>{user.email}</TableData>
              <TableData>{user.password}</TableData>
							<TableData>
                <Link to={`/list/${index+1}`}>View Details</Link>
                <Button onClick={() => handleDelete(index+1)}>Delete</Button>
              </TableData>
            </tr>
          ))}
        </tbody>
      </Table>

			{/* 페이지 번호 출력 */}
			<div style={{"display":"flex", "justifyContent":"center"}}>
        {Array.from({ length: totalPages }, (_, index) => (
          <PageNumber
            key={index}
            onClick={() => handlePageClick(index)}
            active={index === page} // 현재 페이지는 스타일 다르게
          >
            {index + 1}
          </PageNumber>
        ))}
      </div>

			{/* 페이지 이동 버튼 */}
			<div style={{"display":"flex", "justifyContent":"flex-end"}}>
        <PageButton onClick={() => setPage(page > 0 ? page - 1 : 0)} disabled={page === 0}>
          Previous
        </PageButton>
        <PageButton onClick={() => setPage(page < totalPages - 1 ? page + 1 : totalPages - 1)} disabled={page === totalPages - 1}>
          Next
        </PageButton>
      	<PageButton onClick={() => window.location.reload()}>Refresh</PageButton>
      </div>
    </UserListContainer>
  );
};

export default List;
